  };
}

interface UserPreferences {
  colorScheme?: string;
  priceRange?: [number, number];
  categories?: string[];
  brands?: string[];
}

interface TrackingData {
  scrollDepth?: number;
  timeOnPage?: number;
  heatmapPoint?: { x: number; y: number; intensity: number };
}

export const useAI = () => {
  const [recommendations, setRecommendations] = useState<AIRecommendation[]>([]);
